if (window.mapConfig === undefined) {
    window.mapConfig = {
        isLoaded: false,
        scale: 1,
        sections: {},
        newSection: {
            cords: [],
            shape: null
        }
    };
    window.addEventListener('resize', function(event) {
        window.mapConfig.stage.update();
    });
}

window.drawMap = function (mapData) {
    if (!window.mapConfig.stage) {
        window.mapConfig.stage = new createjs.Stage("mapCanvasSectionManager");
        window.mapConfig.stage.enableMouseOver(10);
    }
    window.mapConfig.stage.removeAllChildren();
    var bgImg = document.createElement("img");
    window.mapConfig.stage.canvas.width = window.mapConfig.stage.canvas.parentElement.clientWidth;
    window.mapConfig.stage.canvas.height = window.mapConfig.stage.canvas.parentElement.clientHeight;
    bgImg.map_height = mapData.map_height;
    bgImg.map_width = mapData.map_width;
    bgImg.onload = function() {
        var bg = new createjs.Bitmap(bgImg);
        window.mapConfig.scale = determineScale(bgImg.map_width, bgImg.map_height, window.mapConfig.stage.canvas.width, window.mapConfig.stage.canvas.height);
        bg.scale = window.mapConfig.scale;
        //center map on stage
        window.mapConfig.background_x = (window.mapConfig.stage.canvas.width - bg.image.width * bg.scaleX) / 2;
        bg.x = window.mapConfig.background_x;
        window.mapConfig.background_y = (window.mapConfig.stage.canvas.height - bg.image.height * bg.scaleY) / 2;
        bg.y = window.mapConfig.background_y;
        bgChild = window.mapConfig.stage.addChild(bg);
        window.drawSections(mapData.map_name);
        window.mapConfig.stage.update();
    };
    bgImg.src = mapData.map_image;
    document.getElementById("mapNameSectionManager").innerText = mapData.map_display_name;
}

window.drawSections = function (mapName) {
    //clear all old sections from map
    for (sect in window.mapConfig.sections) {
        window.mapConfig.stage.removeChild(window.mapConfig.sections[sect]);
    }
    window.mapConfig.sections = {};
    var sections = CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().sections.latestData();
    for (var x = 0; x < sections.length; x++) {
        if (sections[x].data && sections[x].data.map_name === mapName) {
            var sectionCords = JSON.parse(sections[x].data.coordinates);
            var sectionShape = new createjs.Shape();
            for (var y = 0; y < sectionCords.length; y++) {
                if (y === 0) {
                    sectionShape.graphics.beginFill(sections[x].data.section_color);
                    sectionShape.graphics.moveTo((sectionCords[y].x * window.mapConfig.scale) + window.mapConfig.background_x, (sectionCords[y].y * window.mapConfig.scale) + window.mapConfig.background_y);
                } else {
                    sectionShape.graphics.lineTo((sectionCords[y].x * window.mapConfig.scale) + window.mapConfig.background_x, (sectionCords[y].y * window.mapConfig.scale) + window.mapConfig.background_y);
                }
                //if last section cord, draw final line to first cord
                if (y === sectionCords.length - 1) {
                    sectionShape.graphics.lineTo((sectionCords[0].x * window.mapConfig.scale) + window.mapConfig.background_x, (sectionCords[0].y * window.mapConfig.scale) + window.mapConfig.background_y);
                }
            }
            window.mapConfig.sections[sections[x].data.section_name] = sectionShape;
            window.mapConfig.stage.addChild(sectionShape);
        }
    }
    window.mapConfig.stage.update();
}

window.handleMapNewSectionClick = function (evt) {
    if (window.mapConfig.newSection.cords.length === 0) {
        var newSectionShape = new createjs.Shape();
        newSectionShape.graphics.beginStroke(window.mapConfig.newSection.color).beginFill(window.mapConfig.newSection.color).moveTo(evt.stageX, evt.stageY);
        window.mapConfig.stage.addChild(newSectionShape);
        window.mapConfig.newSection.shape = newSectionShape;
    } else {
        window.mapConfig.newSection.shape.graphics.lineTo(evt.stageX, evt.stageY);
    }
    var newCords = {
        x: (evt.stageX / window.mapConfig.scale) - window.mapConfig.background_x,
        y: (evt.stageY / window.mapConfig.scale) - window.mapConfig.background_y
    };
    window.mapConfig.newSection.cords.push(newCords);
    window.mapConfig.stage.update();
}

