  //add name as title
  var nameTitle = document.createElement("h2");
  nameTitle.innerText = this.datasource.name;
  newAssetDetails.append(nameTitle);
  for (var key in this.datasource) {
    if (this.datasource.hasOwnProperty(key) && key !== "name") {
      var newRow = document.createElement("div");
      newRow.className = "row";
      var label = document.createElement("div");
      label.className = "col-md-6 asset-label";
      label.innerText = key + ":";
      newRow.append(label);
      var value = document.createElement("div");
      value.className = "col-md-6 asset-value";
      //if it's a timestamp, let's make the format consistent, and easily readable
      if (key === "created_date" || key === "last_active_date" || key === "last_location_update") {
        var timestamp;
        if (typeof this.datasource[key] === "string") {
          timestamp = new Date(this.datasource[key]).toLocaleString();
        } else if (this.datasource[key] !== null){
          timestamp = new Date(this.datasource[key]*1000).toLocaleString();
        } else {
          timestamp = "N/A";
        }
        value.innerText = timestamp;
      } else {
        value.innerText = this.datasource[key];
      }
      newRow.append(value);
      newAssetDetails.append(newRow);
    }
  }
  //display configurable asset_type and map_name dropdowns, options loaded from a datasource
  var assetTypes = datasources["assetTypes"].latestData();
  newAssetDetails.append(window.buildSelect(assetTypes, "name", "display_name", "Asset Type", "newAssetTypeSelect"));
  var maps = datasources["maps"].latestData();
  newAssetDetails.append(window.buildSelect(maps, "map_name", "map_display_name", "Map", "newMapSelect"));
  newAssetDetails.append(document.createElement("br"));
  var activateAssetButton = document.createElement("button");
  activateAssetButton.innerText = "Activate Asset";
  activateAssetButton.className = "btn btn-primary";
  activateAssetButton.onclick = function(event) {
    var deviceName = CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().selectedNewAsset.latestData().name;
    var assetType = document.getElementById("newAssetTypeSelect").value;
    var mapName = document.getElementById("newMapSelect").value;
    CB_PORTAL.ClearBlade.Device().updateDeviceByName(deviceName, {"asset_type":assetType, "map_name": mapName}, false, function (err, data) {
      if (err) {
        data = JSON.parse(data);
        var assetDetails = document.getElementById("newAssetDetails");
        var errorMessage = document.createElement("div");
        errorMessage.innerText = "Failed to Activate Device: " + data.error.detail;
        assetDetails.append(errorMessage);
      } else {
        CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().getNewAssets.refresh();
        CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().selectedNewAsset.sendData({});
        document.getElementById("assetActivatedSuccess").className = "";
        document.getElementById("selectANewAssetMessage").className = "";
        document.getElementById("newAssetDetails").className = "hidden";
      }
    });
  };
  newAssetDetails.append(activateAssetButton);
  document.getElementById("selectANewAssetMessage").className = "hidden";
  document.getElementById("assetActivatedSuccess").className = "hidden";
  newAssetDetails.className = "";
} else {
  document.getElementById("selectANewAssetMessage").className = "";
  document.getElementById("newAssetDetails").className = "hidden";
  document.getElementById("assetActivatedSuccess").className = "hidden";
}

if (!window.buildSelect) {
  window.buildSelect = function (arrayOfDataObjects, keyForValue, keyForText, labelText, idForSelect) {
    var container = document.createElement("div");
    container.className = "row";
    var labelDiv = document.createElement("div");
    labelDiv.className = "col-md-6 asset-label";
    labelDiv.innerText = labelText + ":";
    container.append(labelDiv);
    var valueDiv = document.createElement("div");
    valueDiv.className = "col-md-6 asset-value";
    var select = document.createElement("select");
    select.id = idForSelect;
    select.className = "form-control";
    for (var x = 0; x < arrayOfDataObjects.length; x++) {
      var option = document.createElement("option");
      option.value = arrayOfDataObjects[x].data[keyForValue];
      option.text = arrayOfDataObjects[x].data[keyForText];
      select.append(option);
    }
    valueDiv.append(select);
    container.append(valueDiv);
    return container;
  }
}

