//JavaScript
if (this.datasource) {
    var assetTypeDetails = document.getElementById("assetTypeDetails");
    assetTypeDetails.innerHTML = "";
    var img = document.createElement("img");
    img.src = this.datasource.display_icon;
    img.className = "asset-type-image";
    assetTypeDetails.appendChild(img);
    var uploadNewAssetTypeImage = document.createElement("input");
    uploadNewAssetTypeImage.type = "file";
    uploadNewAssetTypeImage.id = "newUpdatedAssetTypeImage";
    assetTypeDetails.appendChild(window.wrapValueWithLabelAndRow("Select a New Asset Type Image:", uploadNewAssetTypeImage, "asset-type"));
    var assetTypeName = document.createElement("div");
    assetTypeName.innerHTML = this.datasource.name;
    assetTypeDetails.appendChild(window.wrapValueWithLabelAndRow("Asset Type Name:", assetTypeName, "asset-type"));
    var assetTypeDisplayName = document.createElement("input");
    assetTypeDisplayName.type = "text";
    assetTypeDisplayName.className = "form-control";
    assetTypeDisplayName.value = this.datasource.display_name;
    assetTypeDisplayName.placeholder = "Asset Type Display Name";
    assetTypeDisplayName.id = "assetTypeDisplayName";
    assetTypeDetails.appendChild(window.wrapValueWithLabelAndRow("Asset Type Display Name:", assetTypeDisplayName, "asset-type"));
    var updateAssetTypeButton = document.createElement("button");
    updateAssetTypeButton.className = "btn btn-primary";
    updateAssetTypeButton.innerText = "Update Asset Type Configuration";
    updateAssetTypeButton.onclick = function (evt) {
        var assetTypeName = CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().selectedAssetType.latestData().name;
        var allAssetTypes = CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().assetTypes.latestData();
        var assetTypeItem;
        for (var x = 0; x < allAssetTypes.length; x++) {
            if (allAssetTypes[x].data.name === assetTypeName) {
                assetTypeItem = allAssetTypes[x];
                break;
            }
        }
        assetTypeItem.data.display_name = document.getElementById("assetTypeDisplayName").value;
        var onItemSave = function (err, data) {
            if (err) {
                alert("Failed to Update Asset Type Configuration");
            } else {
                CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().assetTypes.refresh();
                document.getElementById("assetTypeDetails").className = "hidden";
                document.getElementById("assetTypeUpdatedSuccessfully").className = "";
            }
        };
        var newAssetTypeFile = document.getElementById("newUpdatedAssetTypeImage").files;
        if (newAssetTypeFile.length === 1) {
            var reader = new FileReader();
            reader.readAsDataURL(newAssetTypeFile[0]);
            reader.onload = function () {
                assetTypeItem.data.display_icon = reader.result;
                assetTypeItem.save(onItemSave);
            }
        } else {
            assetTypeItem.save(onItemSave);
        }
    };
    assetTypeDetails.appendChild(updateAssetTypeButton);
    assetTypeDetails.appendChild(document.createElement("br"));
    var deleteAssetTypeButton = document.createElement("button");
    deleteAssetTypeButton.className = "btn btn-danger";
    deleteAssetTypeButton.innerText = "Delete Asset Type Configuration";
    deleteAssetTypeButton.onclick = function (evt) {
        var assetTypeDisplayName = CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().selectedAssetType.latestData().display_name;
        if (window.confirm("Are you sure you want to delete the asset type '" + assetTypeDisplayName + "'?")) {
            var assetTypeName = CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().selectedAssetType.latestData().name;
            var allAssetTypes = CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().assetTypes.latestData();
            for (var x = 0; x < allAssetTypes.length; x++) {
                if (allAssetTypes[x].data.name === assetTypeName) {
                    allAssetTypes[x].destroy(function (err, data) {
                        if (err) {
                            alert("Failed to delete Asset Type");
                        } else {
                            CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().assetTypes.refresh();
                            document.getElementById("assetTypeDeletedSuccessfully").className = "";
                            document.getElementById("assetTypeDetails").className = "hidden";
                        }
                    });
                    break;
                }
            }
        }
    };
    assetTypeDetails.appendChild(deleteAssetTypeButton);
    document.getElementById("assetTypeUpdatedSuccessfully").className = "hidden";
    document.getElementById("assetTypeDeletedSuccessfully").className = "hidden";
    document.getElementById("selectAnAssetTypeMessage").className = "hidden";
    document.getElementById("assetTypeDetails").className = "";
} else {
    document.getElementById("assetTypeUpdatedSuccessfully").className = "hidden";
    document.getElementById("assetTypeDeletedSuccessfully").className = "hidden";
    document.getElementById("selectAnAssetTypeMessage").className = "";
    document.getElementById("assetTypeDetails").className = "hidden";
}

