document.getElementById("submitNewSectionBtn").onclick = function (evt) {
    document.getElementById("submitNewSectionBtn").classList.add("hidden");
    window.mapConfig.stage.removeEventListener("click", window.handleMapNewSectionClick);
    var newSectionObj = {
        method: "INSERT",
        data: {
            section_name: document.getElementById("newSectionName").value,
            coordinates: JSON.stringify(window.mapConfig.newSection.cords),
            map_name: CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().selectedMapSectionManager.latestData().map_name,
            section_color: window.mapConfig.newSection.color
        }
    };
    CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().sections.sendData(newSectionObj).then(function(data) {
        document.getElementById("newSectionName").value = "";
        document.getElementById("newSectionResults").innerText = "Section Successfully Created!";
        document.getElementById("startDrawingSectionBtn").classList.remove("hidden");
        window.mapConfig.stage.removeChild(window.mapConfig.newSection.shape);
        window.mapConfig.newSection = {
            shape: null,
            cords: []
        };
        // var selectedMap = CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().selectedMapSectionManager.latestData();
        // window.drawSections(selectedMap.map_name);
    }, function (err) {
        document.getElementById("newSectionResults").innerText = "Failed to create new section: " + err;
        document.getElementById("startDrawingSectionBtn").classList.remove("hidden");
        window.mapConfig.stage.removeChild(window.mapConfig.newSection.shape);
        window.mapConfig.newSection = {
            shape: null,
            cords: []
        };
    });
}
